INSERT INTO `artists` (`artist_id`, `name`, `phone`, `email`, `bio`, `created_at`, `updated_at`) VALUES
(1, 'John Doe', '0123456789', 'john@example.com', 'Ca sĩ kiêm nhạc sĩ người Mỹ, nổi tiếng với các bản hit như \"Love Story\" và \"Shake It Off\".', NULL, '2024-10-10 06:48:49'),
(2, 'Jane Smith', '0987654321', 'jane@example.com', 'Ca sĩ người Anh nổi bật với phong cách hát nhẹ nhàng, giai điệu ngọt ngào. Những bản hit như \"Shape of You\" và \"Perfect\" đã đạt được thành công toàn cầu.', NULL, NULL),
(3, 'Michael Johnson', '0123498765', 'michael@example.com', 'Nữ ca sĩ người Mỹ với giọng hát cao vút và mạnh mẽ. Cô nổi tiếng với các bản hit như \"Thank U, Next\" và \"7 Rings\".', NULL, NULL),
(4, 'Emily Davis', '0987612345', 'emily@example.com', 'Biểu tượng âm nhạc toàn cầu, Beyoncé không chỉ là ca sĩ mà còn là nhà sản xuất và diễn viên. Các ca khúc như \"Single Ladies\" và \"Halo\" đã đưa cô lên đỉnh cao của sự nghiệp.', NULL, NULL),
(5, 'Robert Wilson', '0123487654', 'robert@example.com', 'Ca sĩ, nhạc sĩ và vũ công tài năng người Mỹ gốc Philippines. Bruno Mars được biết đến với những bài hát như \"Uptown Funk\" và \"Just the Way You Are\".', NULL, NULL),
(6, 'Linda Martinez', '0987543212', 'linda@example.com', 'Ca sĩ kiêm diễn viên nổi tiếng với phong cách thời trang độc đáo và các ca khúc nổi bật như \"Bad Romance\" và \"Shallow\".', NULL, NULL),
(7, 'David Anderson', '0123456543', 'david@example.com', 'Ca sĩ người Canada nổi tiếng từ khi còn rất trẻ. Với các bản hit như \"Baby\" và \"Sorry\", Justin đã chiếm lĩnh bảng xếp hạng âm nhạc quốc tế trong nhiều năm.', NULL, NULL),
(8, 'Susan Thomas', '0987654123', 'susan@example.com', 'Nữ ca sĩ người Mỹ nổi lên với phong cách âm nhạc u ám và chất giọng khác lạ. Cô đã giành nhiều giải Grammy với các ca khúc nổi bật như \"Bad Guy\" và \"Everything I Wanted\".', NULL, NULL),
(9, 'James Taylor', '0123445678', 'james@example.com', 'Ca sĩ người Canada với phong cách R&B đặc trưng. Những bản hit như \"Blinding Lights\" và \"The Hills\" đã đưa anh trở thành một trong những nghệ sĩ hàng đầu thế giới.', NULL, NULL),
(10, 'Patricia Moore', '0987623456', 'patricia@example.com', 'Ca sĩ, diễn viên và doanh nhân người Barbados. Rihanna nổi tiếng với những bản hit toàn cầu như \"Umbrella\" và \"Diamonds\", cùng với thương hiệu thời trang và làm đẹp thành công.', NULL, NULL),
(11, 'Christopher White', '0123467890', 'christopher@example.com', 'Ca sĩ người Mỹ nổi tiếng với các ca khúc đầy cảm xúc như \"Thinking Out Loud\" và \"Castle on the Hill\".', NULL, NULL),
(12, 'Sarah Harris', '0987643210', 'sarah@example.com', 'Nữ ca sĩ và nhà sản xuất âm nhạc người Anh, nổi bật với phong cách nhạc pop-soul và các bản hit như \"Rolling in the Deep\".', NULL, NULL),
(13, 'Daniel Clark', '0123498761', 'daniel@example.com', 'Ca sĩ kiêm nhạc sĩ người Mỹ, nổi tiếng với phong cách đồng quê kết hợp pop trong các ca khúc như \"You Belong With Me\".', NULL, NULL),
(14, 'Karen Lewis', '0987612349', 'karen@example.com', 'Ca sĩ và diễn viên người Mỹ, nổi tiếng với phong cách biểu diễn năng động và các bản hit như \"I Kissed a Girl\" và \"Firework\".', NULL, NULL),
(15, 'Matthew Hall', '0123487659', 'matthew@example.com', 'Ca sĩ người Úc nổi tiếng với phong cách electro-pop và các bản hit như \"Cheap Thrills\" và \"Chandelier\".', NULL, NULL),
(16, 'Laura Walker', '0987532146', 'laura@example.com', 'Ca sĩ và nhạc sĩ người Mỹ nổi tiếng với phong cách hip-hop và các bản hit như \"Old Town Road\".', NULL, NULL),
(17, 'Mark Young', '0123465789', 'mark@example.com', 'Ca sĩ và nhà sản xuất âm nhạc người Anh, nổi bật với phong cách pop và R&B trong các bản hit như \"Stay With Me\".', NULL, NULL),
(18, 'Lisa Allen', '0987654231', 'lisa@example.com', 'Ca sĩ người Mỹ nổi tiếng với phong cách alternative rock và các ca khúc như \"Radioactive\".', NULL, NULL),
(19, 'Kevin King', '0123478901', 'kevin@example.com', 'Nữ ca sĩ người Anh với phong cách nhạc pop và dance nổi bật, nổi tiếng với các bản hit như \"New Rules\".', NULL, NULL),
(20, 'Jessica Wright', '0987643219', 'jessica@example.com', 'Ca sĩ người Mỹ nổi bật với phong cách hip-hop và các bản hit như \"Rockstar\" và \"Circles\".', NULL, NULL),
(21, 'Chi Pu', '0347234789', 'chipu@gmail.com', 'Nguyễn Thùy Chi (sinh ngày 14 tháng 6 năm 1993), thường được biết đến với nghệ danh Chi Pu, là một nữ ca sĩ, diễn viên, người dẫn chương trình kiêm người mẫu người Việt Nam. Cô được công chúng biết đến sau khi lọt vào Top 20 Miss Teen Việt Nam 2009,[1] và', NULL, NULL);

