INSERT INTO `events` (`event_id`, `name`, `artist_id`, `start_time`, `end_time`, `location_id`, `event_type_id`, `description`, `image_url`, `status`, `ticket_sale_start`, `ticket_sale_end`, `created_at`, `updated_at`) VALUES
(1, 'ELF THE MUSICAL JR', 1, '2025-01-16 18:00:00', '2025-01-16 19:00:00', 1, 4, 'Set against the festive backdrop of Christmas in New York City, Elf Jr. explores the timeless and cross-cultural themes of family, acceptance, and unrelenting joy. The story follows Buddy, a human raised by elves at the North Pole, as he embarks on a journey to find his real father in New York City. Along the way, he discovers the true meaning of family and the importance of spreading holiday cheer. No matter how or when you celebrate the New Year, you won\'t want to miss this joyful celebration of the winter season!', 'https://salt.tkbcdn.com/ts/ds/81/8d/ae/973b5195f26f71441d3f131317dcb4bf.jpg', 'sắp diễn ra', '2024-12-10', '2025-01-09', NULL, NULL),
(2, 'MT Fest Concert Tour', 2, '2024-12-21 15:00:00', '2024-12-21 23:00:00', 2, 2, 'MT Fest Concert Tour 2024 khởi động với điểm dừng chân đầu tiên tại Cần Thơ. Chuỗi mở màn Cần Thơ X\'mas MT Fest hứa hẹn là lễ hội âm nhạc bùng nổ nhất mùa Noel tại miền Tây! Lần đầu tiên, dàn nghệ sĩ đình đám từ Anh Trai Say Hi sẽ đổ bộ, mang đến những màn trình diễn đỉnh cao cùng loạt hit “gây sốt”. Đây là cơ hội để giới trẻ miền Tây gặp gỡ thần tượng, hoà mình vào không gian âm nhạc sôi động và tạo nên kỷ niệm đáng nhớ mùa lễ hội này \r\n\r\nGiáng sinh không chỉ là ngày lễ của niềm vui và những món quà, mà còn là dịp đặc biệt để mọi người cùng nhau ra đường, tận hưởng không khí lễ hội và chia sẻ khoảnh khắc bên gia đình, bạn bè', 'https://salt.tkbcdn.com/ts/ds/55/3b/22/3cf8695c5dfbe6595424c7ca23a7aea4.jpg', 'sắp diễn ra', '2024-12-01', '2024-12-17', NULL, NULL),
(3, '2024 SUPER JUNIOR-D&E WORLD TOUR : ECLIPSE in HO CHI MINH CITY', 3, '2024-12-28 17:00:00', '2024-12-28 19:30:00', 3, 3, '2024 SUPER JUNIOR-D&E WORLD TOUR : ECLIPSE in HO CHI MINH CITY\n\n2024 SUPER JUNIOR-D&E WORLD TOUR : ECLIPSE is the highly anticipated concert series by the unit sub-group of the legendary K-pop boy band SUPER JUNIOR, consisting of members Donghae and Eunhyuk. Known for their powerful performances, charismatic stage presence, and strong bond, Donghae and Eunhyuk will take fans on an unforgettable musical journey around the globe with their new tour.\n\nIt is set to be a spectacular event that blends incredible music, stunning visuals, and the kind of energy that only SUPER JUNIOR-D&E can bring to the stage. Whether you\'re a long-time SUPER JUNIOR fan or new to the SUPER JUNIOR-D&E unit, this tour promises to be an unforgettable experience!', 'https://salt.tkbcdn.com/ts/ds/20/f6/82/65d28f94512e511e56be3e53e88eb8ab.jpg', 'sắp diễn ra', '2024-12-01', '2024-12-20', NULL, NULL),
(4, '2024-25 2NE1 ASIA TOUR [WELCOME BACK] IN HO CHI MINH', 4, '2024-12-16 09:47:27', '2024-12-16 09:47:27', 4, 2, '2NE1 WELCOME BACK 2024-25 ASIA TOUR IN HO CHI MINH\r\n2NE1, nhóm nhạc nữ huyền thoại của K-pop gồm CL, BOM, DARA và MINZY, ra mắt năm 2009 dưới sự quản lý của YG Entertainment, nhanh chóng gây tiếng vang lớn với loạt hit đình đám như \"Fire,\" \"I’m the Best\" và \"Come Back Home.\" Với phong cách trình diễn táo bạo và tầm ảnh hưởng vượt bậc, họ trở thành nhóm nữ K-pop đầu tiên thực hiện world tour và ghi dấu trên bảng xếp hạng Billboard 200 với album CRUSH.\r\n\r\nTái hợp sau hơn một thập kỷ, 2NE1 sẽ mang tour diễn WELCOME BACK Asian Tour 2024-25 đến TP. Hồ Chí Minh vào ngày 15-16/2/2025, hứa hẹn mang đến cho người hâm mộ Việt Nam những màn trình diễn bùng nổ và khó quên.', 'https://salt.tkbcdn.com/ts/ds/a2/2a/bc/29d53bc3ec2332fc8b659219fb42cd02.png', 'sắp diễn ra', '2024-12-01', '2024-12-17', NULL, NULL),
(5, 'Christmas Snow Town - Trải nghiệm Giáng sinh giữa ngôi làng tuyết trắng', 7, '2024-12-25 08:00:00', '2024-12-25 22:00:00', 5, 3, 'Bạn đã từng mơ ước được đón Giáng sinh tại ngôi làng phủ ngập tràn tuyết trắng? Mùa lễ hội này, giấc mơ ấy sẽ trở thành hiện thực ngay tại rooftop của GEM Center –  Ngôi làng tuyết Giáng sinh trên tầng cao với vô vàn những thú vị đang chờ bạn khám phá!\r\n\r\nNhững trải nghiệm không thể bỏ lỡ:\r\n\r\nThưởng thức món ăn giáng sinh truyền thống với gà tây nướng, bò hầm, bánh khúc cây, marshmallow nướng, tháp sô-cô-la fondue và các loại thức uống hấp dẫn\r\nChìm đắm giữa khung cảnh tuyết rơi huyền ảo trong 4 khung giờ mỗi ngày của Snow Town\r\nThưởng thức chuỗi đêm nhạc Giáng sinh đặc sắc trình diễn mỗi tối trong suối 47 ngày\r\nPhiếu quà tặng bí mật từ cư dân làng tuyết cho mọi vị khách ghé thăm\r\nMua sắm quà tặng và đồ trang trí Giáng sinh độc đáo\r\nTrải nghiệm hàng loạt hoạt động tương tác hấp dẫn\r\nĐiều khoản và điều kiện:\r\n\r\n[Chính sách hoàn vé, hủy vé]\r\n\r\nVé sự kiện đã đặt online không thể hoàn tiền hoặc đổi trả dưới bất kỳ hình thức nào. Chúng tôi khuyến khích khách hàng xem xét kỹ lưỡng trước khi đặt vé để đảm bảo trải nghiệm tốt nhất. Mọi thắc mắc, xin vui lòng liên hệ bộ phận hỗ trợ của chúng tôi để được tư vấn thêm.\r\n\r\nTuy nhiên, đối với những yêu cầu hoàn tiền, chúng tôi chỉ hoàn tiền trong các trường hợp sau:\r\n\r\nSự kiện bị hủy hoặc thay đổi thông tin không phù hợp với cam kết.\r\nGiao dịch không thành công do lỗi hệ thống nhưng số tiền đã bị trừ.\r\nĐơn hàng bị hủy trước khi vé được gửi hoặc sử dụng.\r\n[Quy trình tiếp nhận và Giải quyết khiếu nại]', 'https://salt.tkbcdn.com/ts/ds/01/5a/24/21ae5a28949aac8de6c43f289ce925c9.png', 'sắp diễn ra', '2024-12-17', '2024-12-22', NULL, NULL),
(6, 'Đà Lạt Chill - Bốn Mùa Yêu', 21, '2024-12-28 18:00:00', '2024-12-28 22:00:00', 6, 1, 'Giới thiệu sự kiện:\r\nĐà Lạt Chill, Bốn Mùa Yêu Concert là một hành trình cảm xúc, đắm chìm trong những giai điệu ngọt ngào và tươi trẻ, hòa quyện với vẻ đẹp thiên nhiên của Đà Lạt qua bốn mùa. Kể nên những câu chuyện \"YÊU\" qua mỗi mùa hoa ở Đà Lạt. Với sự hòa quyện giữa âm nhạc, cảm xúc tạo nên một nghệ thuật đa chiều, giúp khán giả không chỉ trải nghiệm tình yêu qua bốn mùa của thành phố mà còn cảm nhận sự kết nối giữa những thế hệ, giữa những giai đoạn khác nhau trong cuộc đời mỗi người. Một đêm nhạc không chỉ để thưởng thức mà còn để sẻ chia và hồi tưởng.', 'https://salt.tkbcdn.com/ts/ds/3e/ca/58/d5cc98ee69c361bc4369ed444b678e19.jpeg', 'sắp diễn ra', '2024-12-17', '2024-12-26', NULL, NULL),
(7, 'Nhà Hát Kịch IDECAF: Dưới Bóng Giai Nhân', 6, '2025-01-05 19:30:00', '2025-01-05 22:30:00', 6, 4, 'Dưới Bóng Giai Nhân sẽ là một cách kể khác về Kiều và những cái tên tưởng chừng như đã rất quen thuộc. Thêm vào đó một số nhân vật và sự kiện hư cấu không có trong nguyên tác.\r\n\r\n- Tác Giả: Quang Thảo\r\n\r\n- Đạo Diễn: Quang Thảo\r\n\r\n- Diễn Viên: Thanh Thủy, Hồng Ánh, Hoàng Trinh, Mỹ Duyên, Tuyền Mập, Bạch Long, Đình Toàn, Đại Nghĩa, Công Danh, Trịnh Minh Dũng, Phạm Hùng, Kan Lê, Ngọc Nguyên, Minh Sang, Hữu Đạt, Phạm Hạnh, Thanh Anh, Trúc My, Bích Trâm, Bảo Cường, Nhà Hát Thiếu Nhi NỤ CƯỜI với sự góp mặt đặc biệt của NS Diệu Đức.', 'https://salt.tkbcdn.com/ts/ds/04/0e/9e/ea0a3d4e97483603a68dcf3b14fb6289.jpg', 'sắp diễn ra', '2024-12-17', '2024-12-31', NULL, NULL),
(8, 'HA NOI - THE ARTIFICIAL PARADISE TOUR 2025', 16, '2025-01-25 20:00:00', '2025-01-25 22:30:00', 3, 1, 'THE ARTIFICIAL PARADISE TOUR LIVE IN VIETNAM\r\nVIỆT NAM – Midas Promotions hân hạnh thông báo OneRepublic sẽ lần đầu tiên mang tour diễn đầy kỳ vọng, “The Artificial Paradise Tour 2025”, đến Việt Nam! Ban nhạc sẽ khuấy động sân khấu tại Cung Điền Kinh Mỹ Đình, Hà Nội, vào ngày 24 tháng 1 năm 2025, và tiếp nối với một đêm nhạc bùng nổ tại Trung tâm Hội nghị và Triển lãm Sài Gòn (SECC), TP. Hồ Chí Minh, vào ngày 26 tháng 1 năm 2025. \r\n\r\nOneRepublic, ban nhạc từng nhận đề cử GRAMMY®, gồm có ca sĩ chính đồng thời cũng là nhạc sĩ Ryan Tedder, cùng các thành viên Zach Filkins (guitar), Drew Brown (guitar), Brian Willett (đàn phím), Brent Kutzle (bass và cello), và Eddie Fisher (trống). Ban nhạc đã ra mắt album đầu tay Dreaming Out Loud vào năm 2007, với đĩa đơn đình đám “Apologize” bán ra 20 triệu bản, phá vỡ kỷ lục toàn cầu về doanh thu kỹ thuật số và phát sóng, đồng thời nhận được đề cử Grammy. Tiếp nối là album Waking Up (2009) với các hit “All the Right Moves,” “Secrets,” và “Good Life”. Album Native (2013), đạt chứng nhận bạch kim, nổi bật với ca khúc No.1 \"Counting Stars\" đã bán ra 41 triệu bản.\r\n\r\n OneRepublic phát hành album Oh My My vào năm 2016 và đã đạt hơn 5 tỷ lượt nghe trên Spotify tính đến nay. Album mới nhất của họ, Human, ra mắt vào ngày 27 tháng 8 năm 2021, với những đĩa đơn ấn tượng như “Someday,” “Run,” và “Rescue Me,” tổng cộng đã thu về hơn 2,5 tỷ lượt nghe toàn cầu. \r\n\r\nVào cuối năm 2023, OneRepublic đã phát hành ca khúc “Dear Santa” dành riêng cho mùa lễ hội. Trước đó, đầu năm, họ ra mắt các đĩa đơn “Runaway” và “Mirage” (nhạc nền cho trò chơi Assassin’s Creed Mirage), cùng với bản hit đình đám “I Ain’t Worried” từ bộ phim bom tấn Top Gun: Maverick. \r\n\r\nGần đây nhất, OneRepublic đã ra mắt album phòng thu thứ sáu của mình, Artificial Paradise, vào ngày 12 tháng 7 năm 2024. Album bao gồm ca khúc “I Don’t Wanna Wait” hợp tác cùng David Guetta và “Nobody (from Kaiju No. 8)”, được viết riêng cho bài hát kết thúc của bộ anime nổi tiếng Kaiju No. 8. \r\n\r\nOneRepublic: The Artificial Paradise Tour 2025 vinh dự được phối hợp và hỗ trợ bởi Trung tâm Tổ chức Biểu diễn Nghệ thuật và Sự kiện Văn hóa - Nhà hát Lớn Hà Nội, trực thuộc Bộ Văn hóa, Thể thao và Du lịch. \r\n\r\nVé cho OneRepublic: The Artificial Paradise Tour 2025 tại Cung Điền Kinh Mỹ Đình, Hà Nội (ngày 24/01/2025) và Trung tâm Hội nghị và Triển lãm Sài Gòn (SECC), TP. Hồ Chí Minh (ngày 26/01/2025) sẽ chính thức được mở bán từ ngày 29/11/2024 trên ticketbox.vn. \r\n\r\nĐể cập nhật thông tin chi tiết và những tin tức mới nhất, hãy theo dõi OneRepublic và Midas Promotions trên các trang mạng xã hội hoặc truy cập vào website chính thức của họ', 'https://salt.tkbcdn.com/ts/ds/f0/ca/7c/6e7dad78e37fd71064f8c6a957570bd1.jpg', 'sắp diễn ra', '2025-01-08', '2025-01-15', NULL, NULL),
(9, 'LULULOLA SHOW TĂNG PHÚC - PHAN ĐÌNH TÙNG | KẺ QUA ĐƯỜNG', 17, '2025-01-04 17:30:00', '2025-01-04 19:30:00', 1, 1, 'Lululola Show - Hơn cả âm nhạc, không gian lãng mạn đậm chất thơ Đà Lạt bao trọn hình ảnh thung lũng Đà Lạt, được ngắm nhìn khoảng khắc hoàng hôn thơ mộng đến khi Đà Lạt về đêm siêu lãng mạn, được giao lưu với thần tượng một cách chân thật và gần gũi nhất trong không gian ấm áp và không khí se lạnh của Đà Lạt. Tất cả sẽ  mang đến một đêm nhạc ấn tượng mà bạn không thể quên khi đến với Đà Lạt.', 'https://salt.tkbcdn.com/ts/ds/b1/be/2b/30eb3d834503c5f93eb75cee7191c5cf.png', 'sắp diễn ra', '2024-12-17', '2024-12-31', NULL, NULL),
(10, 'MT Fest Concert Tour', 16, '2024-12-21 15:00:00', '2024-12-21 23:00:00', 2, 2, 'MT Fest Concert Tour 2024 khởi động với điểm dừng chân đầu tiên tại Cần Thơ. Chuỗi mở màn Cần Thơ X\'mas MT Fest hứa hẹn là lễ hội âm nhạc bùng nổ nhất mùa Noel tại miền Tây! Lần đầu tiên, dàn nghệ sĩ đình đám từ Anh Trai Say Hi sẽ đổ bộ, mang đến những màn trình diễn đỉnh cao cùng loạt hit “gây sốt”. Đây là cơ hội để giới trẻ miền Tây gặp gỡ thần tượng, hoà mình vào không gian âm nhạc sôi động và tạo nên kỷ niệm đáng nhớ mùa lễ hội này \r\n\r\nGiáng sinh không chỉ là ngày lễ của niềm vui và những món quà, mà còn là dịp đặc biệt để mọi người cùng nhau ra đường, tận hưởng không khí lễ hội và chia sẻ khoảnh khắc bên gia đình, bạn bè', 'https://salt.tkbcdn.com/ts/ds/55/3b/22/3cf8695c5dfbe6595424c7ca23a7aea4.jpg', 'sắp diễn ra', '2024-12-10', '2024-12-22', NULL, NULL),
